import com.eviware.soapui.impl.wsdl.teststeps.WsdlPropertiesTestStep
import com.eviware.soapui.support.GroovyUtils
import soap_pack.ExtCallScript

//com.eviware.soapui.SoapUI.logMonitor.getLogArea("script log").clear()
def con = ExtCallScript.createContext(testRunner, "registerOrderValidation", "registerOrder")
def groovyUtils = new GroovyUtils(con)

def iter = testRunner.testCase.getTestStepsOfType(WsdlPropertiesTestStep.class).iterator()
def i = 0
while (iter.hasNext()) {
	def node = iter.next()
	if (node.getLabel() != "correctData (29)" & node.getLabel() != "withoutTags (38)" & node.getLabel() !="emptyTags (28)") {
		def iterProp = node.getPropertyList().iterator()
		while (iterProp.hasNext()) {
			i++
			def nodeProp = iterProp.next()

			ExtCallScript.execTestStep (testRunner, "registerOrderValidation", "generateCorrectXml")
			def holder = groovyUtils.getXmlHolder( "registerOrder#Request" )
			if (node.getLabel() == "maxLength (25)") {
				if (nodeProp.getName() == "//items/@positionId") {
					holder.setNodeValue("//loyalties/@positionId", nodeProp.getValue())
				}
				if (nodeProp.getName() == "//itemAmount") {
					holder.setNodeValue("//order/@amount", nodeProp.getValue())
				}
				if (nodeProp.getName() == "//loyalties/@positionId") {
					holder.setNodeValue("//items/@positionId", nodeProp.getValue())
				}
				if (nodeProp.getName() == "//bonusAmountForDebit") {
					holder.setNodeValue("//params/@value", nodeProp.getValue())
				}
			}
			holder.setNodeValue(nodeProp.getName(),nodeProp.getValue())
			holder.updateProperty()
			ExtCallScript.execTestStep (testRunner, "registerOrderValidation", "registerOrder")
			holder = groovyUtils.getXmlHolder( "registerOrder#Response" )
			log.info "---" + " " +  i + " " + "--- " + " " + node.getName()+ " "+ nodeProp.getName()+" errorCode=" +holder["//return/@errorCode"]+" errorMessage="+holder["//return/@errorMessage"]+ " "+ holder["//return/@orderId"]
		}
	}
}

import com.eviware.soapui.impl.wsdl.teststeps.WsdlPropertiesTestStep
import com.eviware.soapui.support.GroovyUtils
import soap_pack.ExtCallScript

def con = ExtCallScript.createContext(testRunner, "registerOrderValidation", "registerOrder")
def groovyUtils = new GroovyUtils(con)

def testStep = testRunner.testCase.getTestStepByName("withoutTags")


def iter = testStep.getPropertyList().iterator()
def i = 0
while (iter.hasNext()) {
	i++
	ExtCallScript.execTestStep(testRunner, "registerOrderValidation", "generateCorrectXml")

	def holder = groovyUtils.getXmlHolder( "registerOrder#Request" )
	def node = iter.next()
	def xpath = node.getName().split("/@")
	def xp_node = xpath[0]


	if (holder.getDomNode.hasAttributes) {
		def attr = xpath[1]
		holder.getDomNode(xp_node).getAttributes().removeNamedItem(attr)
	}
	else holder.removeDomNodes(xp_node)

	holder.updateProperty()
	ExtCallScript.execTestStep (testRunner, "registerOrderValidation", "registerOrder")
	holder = groovyUtils.getXmlHolder("registerOrder#Response")
	log.info "---" + " " +  i + " " + "--- " + " " + node.getName()+ " errorCode=" +holder["//return/@errorCode"]+" errorMessage="+holder["//return/@errorMessage"]//+ " "+ holder["//return/@orderId"]
}

import com.eviware.soapui.impl.wsdl.teststeps.WsdlPropertiesTestStep
import com.eviware.soapui.support.GroovyUtils
import soap_pack.ExtCallScript

def con = ExtCallScript.createContext(testRunner, "registerOrderValidation", "registerOrder")
def groovyUtils = new GroovyUtils(con)

def testStep = testRunner.testCase.getTestStepByName("emptyTags")


def iter = testStep.getPropertyList().iterator()
def i = 0
while (iter.hasNext()) {
	i++
	ExtCallScript.execTestStep(testRunner, "registerOrderValidation", "generateCorrectXml")

	def holder = groovyUtils.getXmlHolder( "registerOrder#Request" )
	def node = iter.next()
	def xpath = node.getName().split("/@")
	def xp_node = xpath[0]


	if (holder.getDomNode.hasAttributes) {
		def attr = xpath[1]
		holder.getDomNode(xp_node).getAttributes().getNamedItem(attr).setNodeValue("")
	}
	else holder.setNodeValue(xp_node, null) 

	holder.updateProperty()
	ExtCallScript.execTestStep (testRunner, "registerOrderValidation", "registerOrder")
	holder = groovyUtils.getXmlHolder("registerOrder#Response")
	log.info "---" + " " +  i + " " + "--- " + " " + node.getName()+ " errorCode=" +holder["//return/@errorCode"]+" errorMessage="+holder["//return/@errorMessage"]+ " "+ holder["//return/@orderId"]
}

