import com.eviware.soapui.impl.wsdl.teststeps.WsdlPropertiesTestStep
import com.eviware.soapui.support.GroovyUtils
import soap_pack.ExtCallScript

def con = ExtCallScript.createContext(testRunner, "registerOrderPreAuthValidation", "registerOrderPreAuth")
def groovyUtils = new GroovyUtils(con)
def i = 0
def iter = testRunner.testCase.getTestStepsOfType(WsdlPropertiesTestStep.class).iterator()
while (iter.hasNext()) {
	i++
	ExtCallScript.execTestStep (testRunner, "registerOrderPreAuthValidation", "generateCorrectXml")
	def node = iter.next()
	def holder = groovyUtils.getXmlHolder( "registerOrderPreAuth#Request" )
	def items = holder.getDomNode("//items").cloneNode(true);	
	def loyalties = holder.getDomNode("//loyalties[@positionId=\"хохо\"]").cloneNode(true);
	ExtCallScript.fillProperties(holder, node)
	if (node.getLabel() == "items+, loyalties- (1)") {			    							 //Отправить запрос с items, но без loyalties
		holder.removeDomNodes("//loyalties");
		holder.getDomNode("//cartItems").appendChild(items)
		holder.setNodeValue("//items/@positionId", "хаха")
	}
	if (node.getLabel() == "items-, loyalties+ (1)") {			       						//Отправить запрос с loyalties, но без items
		holder.removeDomNodes("//items");
		holder.getDomNode("//loyalties").appendChild(loyalties)
		holder.setNodeValue("//loyalties/@positionId", "хаха")
	}
	if (node.getLabel() == "aFewLoyaltiesToOneItem (1)") {							//Несколько loyalties к одному товару
		holder.getDomNode("//loyalties").appendChild(loyalties)
	}
	if (node.getLabel() == "differentItemsOrderInItemsAndLoyalties (2)") {         //В loyalties один порядок товаров, в items другой порядок товаров
		holder.getDomNode("//cartItems").appendChild(items)
		holder.setNodeValue("//items/@positionId", "хаха")
		holder.getDomNode("//loyalties").appendChild(loyalties)
		holder.setNodeValue("//loyalties[2]/@positionId", "хаха")
	}
	if (node.getLabel() == "itemsWithTheSamePositionIds (1)") {         				//Отправка запросов с товарами с одинаковыми positionIds
		holder.getDomNode("//cartItems").appendChild(items)	
	}
	if (node.getLabel() == "bonusesParams>99%ofOrderAmount(loyalties-) (3)" | node.getLabel() == "bonusesParams<99%ofOrderAmount(loyalties-) (3)" | node.getLabel() == "bonusesParams=99%ofOrderAmount(loyalties-) (3)"  ) {         				//Отправка запросов с товарами с одинаковыми positionIds
		holder.removeDomNodes("//loyalties")
	}
	holder.updateProperty()	
	ExtCallScript.execTestStep (testRunner, "registerOrderPreAuthValidation", "registerOrderPreAuth")
	holder = groovyUtils.getXmlHolder("registerOrderPreAuth#Response")	
	log.info "---" + i + "--- " +node.getLabel()+ " errorCode=" +holder["//return/@errorCode"]+" errorMessage="+holder["//return/@errorMessage"]
}

log.info "=== Ready ==="

