import soap_pack.ExtCallScript

def conReg = ExtCallScript.createContext(testRunner, "registerOrderPreAuthValidation", "registerOrderPreAuth")
def groovyUtils = new GroovyUtils(conReg)
def conDep = ExtCallScript.createContext(testRunner, "depositOrderBusiness", "depositOrder")
def groovyUtilsDep = new GroovyUtils(conDep)
def conPay = ExtCallScript.createContext(testRunner, "paymentOrder", "paymentOrder")
def groovyUtilsPay = new GroovyUtils(conPay)
def iter = testRunner.testCase.getTestStepsOfType(WsdlPropertiesTestStep.class).iterator()
def i = 0
while (iter.hasNext()) {
	def node = iter.next()
	if (node.getLabel() != "withoutTags (21)" & node.getLabel() !="emptyTags (15)") {
		def iterProp = node.getPropertyList().iterator()
		while (iterProp.hasNext()) {
			i++
			def nodeProp = iterProp.next()
			ExtCallScript.execTestStep (testRunner, "registerOrderPreAuthValidation", "generateCorrectXml")
			ExtCallScript.execTestStep (testRunner, "paymentOrder", "generateCorrectXml")
			ExtCallScript.execTestStep (testRunner, "depositOrderBusiness", "generateCorrectXml")

			def holder = groovyUtilsDep.getXmlHolder("depositOrder#Request")

			holder.setNodeValue(nodeProp.getName(),nodeProp.getValue())
			holder.updateProperty()	

			if (node.getLabel() == "maxLength (13)") {
				if (nodeProp.getName() == "//items/@positionId") {
					holder = groovyUtils.getXmlHolder( "registerOrderPreAuth#Request" )
					holder.setNodeValue("//items/@positionId", nodeProp.getValue())
					holder.setNodeValue("//loyalties/@positionId", nodeProp.getValue())
				}
				if (nodeProp.getName() == "//name") {
					holder = groovyUtils.getXmlHolder( "registerOrderPreAuth#Request" )
					holder.setNodeValue("//name", nodeProp.getValue())
				}
				if (nodeProp.getName() == "//quantity") {
					holder = groovyUtils.getXmlHolder( "registerOrderPreAuth#Request" )
					holder.setNodeValue("//quantity", nodeProp.getValue())
				}
				if (nodeProp.getName() == "//itemCode") {
					holder = groovyUtils.getXmlHolder( "registerOrderPreAuth#Request" )
					holder.setNodeValue("//itemCode", nodeProp.getValue())
				}
//				if (nodeProp.getName() == "//itemAmount") {
//					holder = groovyUtilsDep.getXmlHolder("depositOrder#Request")
//					holder.setNodeValue("//order/@depositAmount", nodeProp.getValue())
//					
//				}
			}
			holder.updateProperty()

			ExtCallScript.execTestStep (testRunner, "registerOrderPreAuthValidation", "registerOrderPreAuth")
			holder = groovyUtils.getXmlHolder("registerOrderPreAuth#Response")
			def orderId = holder.getNodeValue("//return/@orderId")
			holder = groovyUtilsPay.getXmlHolder("paymentOrder#Request")
			holder.setNodeValue("//order/@orderId", orderId)
			holder.updateProperty()	
			holder = groovyUtilsDep.getXmlHolder("depositOrder#Request")
			holder.setNodeValue("//order/@orderId", orderId)
			holder.updateProperty()	
			ExtCallScript.execTestStep (testRunner, "paymentOrder", "paymentOrder")		
			ExtCallScript.execTestStep (testRunner, "depositOrderBusiness", "depositOrder")
			holder = groovyUtilsDep.getXmlHolder("depositOrder#Response")	
			log.info "---" + " " +  i + " " + "--- " + " " + node.getName()+ " "+nodeProp.getName()+" errorCode=" +holder["//return/@errorCode"]+" errorMessage="+holder["//return/@errorMessage"]+ " "+ holder["//return/@orderId"]
		}
	}
}
