import com.eviware.soapui.impl.wsdl.teststeps.WsdlPropertiesTestStep
import com.eviware.soapui.support.GroovyUtils
import soap_pack.ExtCallScript
// Провести частичный депозит с указанием товара
//Провести частичный депозит без указания товара
//itemAmount в депозите < itemAmount в registerOrderPreAuth
//positionId товара в депозите не совпадает с positionId в registerOrderPreAuth
//name товара в депозите не совпадает с name в registerOrderPreAuth
//itemCode товара в депозите не совпадает с itemCode в registerOrderPreAuth
//depositAmount=itemAmount в депозите > itemAmount в registerOrderPreAuth –
//Провести депозит с указанием в корзине нескольких одинаковых товаров, при этом сумма депозита не должна превышать общую денежную сумму при регистрации заказа (registerOrder, registerOrderPreAuth) –
//В корзине депозита больше товаров, чем было в корзине при регистрации заказа -
//Депозит на полную сумму с корзиной +
//Депозит на полную сумму без корзины +
//itemCurrency не указана +
//itemCurrency указана и совпадает с валютой, указанной при регистрации +
//itemCurrency указана и не совпадает с валютой, указанной при регистрации -
//quantity в depositOrder > quantity в registerOrderPreAuth -
//quantity в depositOrder ≤ quantity в registerOrderPreAuth +
//Передать баллы в теге params (должны игнорироваться)
def conReg = ExtCallScript.createContext(testRunner, "registerOrderPreAuthValidation", "registerOrderPreAuth")
def groovyUtils = new GroovyUtils(conReg)
def conDep = ExtCallScript.createContext(testRunner, "depositOrderBusiness", "depositOrder")
def groovyUtilsDep = new GroovyUtils(conDep)
def conPay = ExtCallScript.createContext(testRunner, "paymentOrder", "paymentOrder")
def groovyUtilsPay = new GroovyUtils(conPay)
def iter = testRunner.testCase.getTestStepsOfType(WsdlPropertiesTestStep.class).iterator()
def i = 0
while (iter.hasNext()) {
	i++
	def node = iter.next()
	ExtCallScript.execTestStep (testRunner, "registerOrderPreAuthValidation", "generateCorrectXml")
	ExtCallScript.execTestStep (testRunner, "paymentOrder", "generateCorrectXml")
	ExtCallScript.execTestStep (testRunner, "depositOrderBusiness", "generateCorrectXml")
	def holder = groovyUtilsDep.getXmlHolder("depositOrder#Request")
	ExtCallScript.fillProperties(holder, node)
	holder.updateProperty()	
	if (node.getLabel() == "partialDepositWithoutItems (1)") {								//Провести частичный депозит без указания товара
		holder = groovyUtilsDep.getXmlHolder("depositOrder#Request")
		holder.removeDomNodes("//depositItems")
	}
	if (node.getLabel() == "depItemAmount>regItemAmount (2)") {
		holder = groovyUtils.getXmlHolder("registerOrderPreAuth#Request")
		def items = holder.getDomNode("//items").cloneNode(true);	
		holder.getDomNode("//cartItems").appendChild(items)
		holder.setNodeValue("//items/@positionId", "хаха")
		holder.setNodeValue("//order/@amount", 30000)		
	}
	if (node.getLabel() == "sameItems (2)") {
		holder = groovyUtilsDep.getXmlHolder("depositOrder#Request")
		def itemsDep = holder.getDomNode("//items").cloneNode(true);	
		holder.getDomNode("//depositItems").appendChild(itemsDep)
	}
	if (node.getLabel() == "depItemsQuantity>regItemsQuantity (2)") {
		holder = groovyUtilsDep.getXmlHolder("depositOrder#Request")
		itemsDep = holder.getDomNode("//items").cloneNode(true);	
		holder.getDomNode("//depositItems").appendChild(itemsDep)
		holder.setNodeValue("//items/@positionId", "хаха")
	}
	if (node.getLabel() == "aFewItems (1)") {
		holder = groovyUtils.getXmlHolder("registerOrderPreAuth#Request")
		def items = holder.getDomNode("//items").cloneNode(true);	
		holder.getDomNode("//cartItems").appendChild(items)
		holder.setNodeValue("//items/@positionId", "хаха")
		holder.setNodeValue("//itemAmount[1]", 10000)
		holder.setNodeValue("//order/@amount", 25000)	
		holder.updateProperty()
		holder = groovyUtilsDep.getXmlHolder("depositOrder#Request")	
		itemsDep = holder.getDomNode("//items").cloneNode(true);	
		holder.getDomNode("//depositItems").appendChild(itemsDep)
		holder.setNodeValue("//items/@positionId", "хаха")
		holder.setNodeValue("//itemAmount[1]", 10000)
		holder.setNodeValue("//order/@depositAmount", 25000)	
	}
	holder.updateProperty()
	ExtCallScript.execTestStep (testRunner, "registerOrderPreAuthValidation", "registerOrderPreAuth")
	holder = groovyUtils.getXmlHolder("registerOrderPreAuth#Response")
	def orderId = holder.getNodeValue("//return/@orderId")
	holder = groovyUtilsPay.getXmlHolder("paymentOrder#Request")
	holder.setNodeValue("//order/@orderId", orderId)
	holder.updateProperty()	
	holder = groovyUtilsDep.getXmlHolder("depositOrder#Request")
	holder.setNodeValue("//order/@orderId", orderId)
	holder.updateProperty()	
	ExtCallScript.execTestStep (testRunner, "paymentOrder", "paymentOrder")		
	ExtCallScript.execTestStep (testRunner, "depositOrderBusiness", "depositOrder")
	holder = groovyUtilsDep.getXmlHolder("depositOrder#Response")	
	log.info "---" + " " +  i + " " + "--- " + " " + node.getName()+ " errorCode=" +holder["//return/@errorCode"]+" errorMessage="+holder["//return/@errorMessage"]+ " "+ holder["//return/@orderId"]
}
