	ExtCallScript.execTestStep (testRunner, "paymentOrder", "generateCorrectXml")
	ExtCallScript.execTestStep (testRunner, "refundOrderValidation", "generateCorrectXml")

	def holder = groovyUtilsRef.getXmlHolder("refundOrder#Request")
	ExtCallScript.fillProperties(holder, node)
	//holder.setNodeValue(nodeProp.getName(),nodeProp.getValue())
	holder.updateProperty()
	if (node.getLabel() == "items+, loyalties- (1)") {			    							 //Отправить запрос с items, но без loyalties
		def items = holder.getDomNode("//items").cloneNode(true);	
		holder.getDomNode("//refundItems").appendChild(items)
		holder.setNodeValue("//order/@refundAmount", 2000)
	}
	if (node.getLabel() == "refAmount>payAmount(refundItems-) (1)") {			    							 //Отправить запрос с items, но без loyalties
		def items = holder.getDomNode("//items").cloneNode(true);	
		holder.removeDomNodes("//refundItems")
	}	
	holder.updateProperty()		

	ExtCallScript.execTestStep (testRunner, "registerOrderValidation", "registerOrder")
	holder = groovyUtils.getXmlHolder("registerOrder#Response")
	def orderId = holder.getNodeValue("//return/@orderId")
	holder = groovyUtilsPay.getXmlHolder("paymentOrder#Request")
	holder.setNodeValue("//order/@orderId", orderId)
	holder.updateProperty()	
	holder = groovyUtilsRef.getXmlHolder("refundOrder#Request")
	holder.setNodeValue("//order/@orderId", orderId)
	holder.updateProperty()	
	ExtCallScript.execTestStep (testRunner, "paymentOrder", "paymentOrder")		
	ExtCallScript.execTestStep (testRunner, "refundOrderValidation", "refundOrder")
	holder = groovyUtilsRef.getXmlHolder("refundOrder#Response")	
	log.info "---" + " " +  i + " " + "--- " + " " + node.getName()+ " "+" errorCode=" +holder["//return/@errorCode"]+" errorMessage="+holder["//return/@errorMessage"]
}
log.info "=== Ready ==="

//Провести рефанд несколько раз по одному товару, чтобы итоговая сумма рефанда по товару превысила itemAmount по данному товару, указанному в регистрации заказа или при депозите
import com.eviware.soapui.impl.wsdl.teststeps.WsdlPropertiesTestStep
import com.eviware.soapui.support.GroovyUtils
import soap_pack.ExtCallScript

//com.eviware.soapui.SoapUI.logMonitor.getLogArea("script log").clear()
def conReg = ExtCallScript.createContext(testRunner, "registerOrderValidation", "registerOrder")
def groovyUtils = new GroovyUtils(conReg)
def conPay = ExtCallScript.createContext(testRunner, "paymentOrder", "paymentOrder")
def groovyUtilsPay = new GroovyUtils(conPay)
def conRef = ExtCallScript.createContext(testRunner, "refundOrderValidation", "refundOrder")
def groovyUtilsRef = new GroovyUtils(conRef)
def i = 0
def iter = testRunner.testCase.getTestStepsOfType(WsdlPropertiesTestStep.class).iterator()
while (iter.hasNext()) {
	i++
	ExtCallScript.execTestStep (testRunner, "registerOrderValidation", "generateCorrectXml")
	def node = iter.next()
	ExtCallScript.execTestStep (testRunner, "registerOrderValidation", "generateCorrectXml")
	ExtCallScript.execTestStep (testRunner, "paymentOrder", "generateCorrectXml")
	ExtCallScript.execTestStep (testRunner, "refundOrderValidation", "generateCorrectXml")

	def holder = groovyUtilsRef.getXmlHolder("refundOrder#Request")
	//ExtCallScript.fillProperties(holder, node)
	//holder.setNodeValue(nodeProp.getName(),nodeProp.getValue())
	holder.updateProperty()	
	holder = groovyUtils.getXmlHolder("registerOrder#Request")
	def items = holder.getDomNode("//items").cloneNode(true);	
	holder.getDomNode("//cartItems").appendChild(items)
	holder.setNodeValue("//order/@amount", 30000)
	holder.setNodeValue("//items/@positionId", 1)
	holder.updateProperty()		

	ExtCallScript.execTestStep (testRunner, "registerOrderValidation", "registerOrder")
	holder = groovyUtils.getXmlHolder("registerOrder#Response")
	def orderId = holder.getNodeValue("//return/@orderId")
	holder = groovyUtilsPay.getXmlHolder("paymentOrder#Request")
	holder.setNodeValue("//order/@orderId", orderId)
	holder.updateProperty()	
	holder = groovyUtilsRef.getXmlHolder("refundOrder#Request")
	holder.setNodeValue("//order/@orderId", orderId)
	holder.updateProperty()	
	ExtCallScript.execTestStep (testRunner, "paymentOrder", "paymentOrder")		
	ExtCallScript.execTestStep (testRunner, "refundOrderValidation", "refundOrder")
	holder = groovyUtilsRef.getXmlHolder("refundOrder#Response")	
	log.info "---" + " " +  i + " " + "--- " + " " + node.getName()+ " "+" errorCode=" +holder["//return/@errorCode"]+" errorMessage="+holder["//return/@errorMessage"]
	holder = groovyUtilsRef.getXmlHolder("refundOrder#Request")
	holder.setNodeValue("//itemAmount", 1000)
	holder.setNodeValue("//order/@refundAmount", 1000)
	ExtCallScript.execTestStep (testRunner, "refundOrderValidation", "refundOrder")
	holder = groovyUtilsRef.getXmlHolder("refundOrder#Response")	
	log.info "---" + " " +  i + " " + "--- " + " " + node.getName()+ " "+" errorCode=" +holder["//return/@errorCode"]+" errorMessage="+holder["//return/@errorMessage"]
}
log.info "=== Ready ==="

