def pan			= authTransfer.getPropertyValue( "maskedPan" );
log.info bindingString;
log.info pan;
def jsonObj = new JsonSlurper().parseText(bindingString);
log.info jsonObj;

def maps = jsonObj.bindings.size();
log.info maps;



Iterator i =jsonObj.bindings.iterator();

log.info "====================="
def myBinding;
while ( i.hasNext()) {
		myBinding = i.next();
          if (myBinding.maskedPan == pan & myBinding.cardStatus==0) {
          	authTransfer.setPropertyValue( "bindingId",  myBinding.bindingId);
          	break;
          }
          else log.info "не найдено совпадений";
   		propertiesRegisterTransfer.setPropertyValue( "bindingId", "нет активных связок");
         }

import java.security.MessageDigest
import static Constants.*

class Constants {
    static final STRCHARSET 		= "UTF-8" //Кодировка строки для обработки MD5
 }

def MD5(String s)  {
	MessageDigest md = MessageDigest.getInstance("MD5")
	md.update(s.getBytes(STRCHARSET))
	BigInteger b = new BigInteger(1,md.digest())
	return b.toString(16).padLeft(32,"0")
}

