def terminalNum = Transfer.getPropertyValue("terminalNum")// код терминала
def errorDesc = Transfer.getPropertyValue("errorDesc")// код терминала
def maskedPan = Transfer.getPropertyValue("maskedPan")// код терминала
def mdOrder = Transfer.getPropertyValue("mdOrder")// код терминала
def bindingId = Transfer.getPropertyValue("bindingId")// код терминала
def ismobile = Transfer.getPropertyValue("ismobile")//  
def status = Transfer.getPropertyValue("3dsstatus")//  

	def out = new File(logfile)
	def row = [date, terminalNum,errorCode,errorDesc,maskedPan,mdOrder,ismobile,status,bindingId]
