class Constants {
    static final STRCHARSET 		= "UTF-8" //Кодировка строки для обработки MD5
 }

def MD5(String s)  {
	MessageDigest md = MessageDigest.getInstance("MD5")
	md.update(s.getBytes(STRCHARSET))
	BigInteger b = new BigInteger(1,md.digest())
	return b.toString(16).padLeft(32,"0")
}

