def properties = testRunner.testCase.getTestStepByName( "Properties");

def dataFB		= properties.getPropertyValue("dataFb");

start = dataFB.indexOf("avatar\":");
log.info dataFB
end = dataFB.indexOf("\"",start +9);
image = dataFB.substring(start +9, end);
dataFB=dataFB.replace(image, "");

log.info start
log.info end
log.info image;

def jsonObj = new JsonSlurper().parseText( dataFB);
