def addData (data1){
def inputFile = new File("d:/testData.csv");
def String myBindings;
def int q =0;
def authTransfer = testRunner.testCase.getTestStepByName( "PropertiesPayment");
Iterator it = inputFile.iterator(); 
while (it.hasNext())


	{
	myBindings = it.next();
	def values = myBindings.tokenize(';')
	a = values.size();
//	log.info "id= "+ values[0]; 
	def cont = "\"service id\""+":"+"\""+values[0]+"\"";
//	log.info "ghjdthrf "+cont;
//	log.info "совпадает айди?: "+data1.contains(cont);
//	log.info "точно ли есть ЭТО в файле? "+data1.contains(cont);
	if ( data1.contains(cont)==true)
		{
			for (q=0;q<a;q++)
			{
			log.info "value"+q+"="+values[q];
			def value = values[q];
			data1=data1.replace("value"+q,value);
			}
			log.info data1;
			log.info "================";
			return data1;

		}
	}
}

