def auth()
{
	project = testRunner.testCase.testSuite.project;
authTransfer = testRunner.testCase.getTestStepByName( "PropertiesPayment");
propsAuth = testRunner.testCase.getTestStepByName("auth");
				propsAuth.setPropertyValue("username","0002981361");
				propsAuth.setPropertyValue("login","8042830");	
				testRunner.runTestStep( project.testSuites["testGroovy"].testCases["TestCase 1"].testSteps["auth"] )

				sessionId= propsAuth.getPropertyValue( "sessionId");
				userId=propsAuth.getPropertyValue( "userId");
				isMobile=propsAuth.getPropertyValue( "isMobile");

				authTransfer.setPropertyValue( "sessionId",sessionId);
				authTransfer.setPropertyValue( "userId",userId);
				authTransfer.setPropertyValue( "isMobile",isMobile);


}
def getPaymentCapabilities()
{

project = testRunner.testCase.testSuite.project;
authTransfer = testRunner.testCase.getTestStepByName( "PropertiesPayment");
props = testRunner.testCase.getTestStepByName("getPaymentCapabilities");

			sessionId= authTransfer.getPropertyValue( "sessionId");
			userId=authTransfer.getPropertyValue( "userId");
			serviceId=authTransfer.getPropertyValue( "serviceId");	
			isMobile=authTransfer.getPropertyValue( "isMobile");
			details=authTransfer.getPropertyValue( "details");

			props.setPropertyValue( "sessionId",sessionId);
			props.setPropertyValue( "userId",userId);
			props.setPropertyValue( "serviceId_start",serviceId);	
			props.setPropertyValue( "isMobile",isMobile);
			props.setPropertyValue( "details",details);			

			log.info "мегапараметр"+props.getPropertyValue( "details");

			testRunner.runTestStep( project.testSuites["testGroovy"].testCases["TestCase 1"].testSteps["getPaymentCapabilities"] )



			//	props.setPropertyValue( "details",s5);
			sessionId= authTransfer.getPropertyValue( "sessionId");
			userId=authTransfer.getPropertyValue( "userId");
			serviceId=props.getPropertyValue( "serviceId");	
			isMobile=props.getPropertyValue( "isMobile");
			details=props.getPropertyValue( "details");
			bindingId=props.getPropertyValue( "bindingId");
			cvv2=props.getPropertyValue( "cvv2");

			authTransfer.setPropertyValue( "serviceId",serviceId);	
			authTransfer.setPropertyValue( "isMobile",isMobile);
			authTransfer.setPropertyValue( "details",details);
			authTransfer.setPropertyValue( "bindingId",bindingId);	
			authTransfer.setPropertyValue( "cvc",cvv2);				

}


def payment3ds()
{

authTransfer = testRunner.testCase.getTestStepByName( "PropertiesPayment");
props = testRunner.testCase.getTestStepByName("getPaymentCapabilities");
propsAuth = testRunner.testCase.getTestStepByName("auth");
propsPayment = testRunner.testCase.getTestStepByName("payment3ds");

			sessionId= propsAuth.getPropertyValue( "sessionId");
			userId=propsAuth.getPropertyValue( "userId");

			bindingId= authTransfer.getPropertyValue( "bindingId");
			serviceId=authTransfer.getPropertyValue( "serviceId");	

			currency=authTransfer.getPropertyValue( "currency");	
			amount=authTransfer.getPropertyValue( "amount");
			isMobile=authTransfer.getPropertyValue( "isMobile");
			cvc=authTransfer.getPropertyValue( "cvc");
			details=authTransfer.getPropertyValue( "details");

			//------------------------
			propsPayment.setPropertyValue( "bindingId", bindingId);
			propsPayment.setPropertyValue( "cvc2", cvc);
			propsPayment.setPropertyValue( "userId", userId);	
			propsPayment.setPropertyValue( "sessionId", sessionId);

			propsPayment.setPropertyValue( "serviceId", serviceId);
			propsPayment.setPropertyValue( "isMobile", isMobile);
			propsPayment.setPropertyValue( "currency", currency);
			propsPayment.setPropertyValue( "amount", amount);
			propsPayment.setPropertyValue( "data", details);			

	testRunner.runTestStep( project.testSuites["testGroovy"].testCases["TestCase 1"].testSteps["payment3ds"] )	;		

}


