def authTransfer = testRunner.testCase.getTestStepByName( "Properties");
def transactionString			= authTransfer.getPropertyValue( "data");
def String size			= authTransfer.getPropertyValue( "size");

log.info transactionString;

def jsonObj = new JsonSlurper().parseText(transactionString);



def String maps = jsonObj.orders.size();

def String status;


if (size == ""){
status  = "количество успешных транзакций: "+ maps;
authTransfer.setPropertyValue( "value",  status);
log.info status;
}
else if (size == maps)
{
status = "количество успешных транзакций: "+ maps;
authTransfer.setPropertyValue( "value",  status);
log.info status;
}

else if (size != "" && size !=maps)
{
status = "количество транзакций не совпадает: getSize:  "+ size + " getPage: " + maps;
authTransfer.setPropertyValue( "value",  status);
log.info status;
}

