import java.util.*;
import java.text.*;
//import org.codehaus.groovy.runtime.TimeCategory;
import org.apache.commons.lang.RandomStringUtils;
import groovy.json.JsonBuilder;
import groovy.json.JsonSlurper;
import groovy.json.JsonOutput;
import java.security.MessageDigest;
import java.util.Iterator;


def authTransfer = testRunner.testCase.getTestStepByName( "Properties");
def transactionString			= authTransfer.getPropertyValue( "data");
def templateName			= authTransfer.getPropertyValue( "templateName");

log.info transactionString;

def jsonObj = new JsonSlurper().parseText(transactionString);

log.info jsonObj;


Iterator i =jsonObj.templates.iterator();

log.info "====================="
def myTemplates;
while ( i.hasNext()) {
		myTemplates = i.next();
          if (myTemplates.templateName == templateName ) {
log.info "значение найдено";
log.info jsonObj.serviceParams;

def String TemplateName= myTemplates.templateName;
def String templateId = myTemplates.templateId;
def String category= myTemplates.category;
def String serviceParams = myTemplates.serviceParams.phone;
def String serviceId = myTemplates.serviceId;
def String currency = myTemplates.currency;
def String bindingId = myTemplates.bindingId;
def String amount = myTemplates.amount;
def paymentDetail = "{\"data\":\""+serviceParams+", \"serviceId\":"+serviceId+"\"}";
log.info paymentDetail;

authTransfer.setPropertyValue("templateName", TemplateName);
authTransfer.setPropertyValue("templateId",templateId);
authTransfer.setPropertyValue("category",category);
authTransfer.setPropertyValue("serviceParams",serviceParams);
authTransfer.setPropertyValue("serviceId",serviceId);
authTransfer.setPropertyValue("currency",currency);
authTransfer.setPropertyValue("bindingId",bindingId);
authTransfer.setPropertyValue("amount",amount);
authTransfer.setPropertyValue("paymentDetail",paymentDetail);

          	break;
          }
          else log.info "не найдено совпадений";

         }

